+      ArcGISMap map = new ArcGISMap(BasemapStyle.ARCGIS_LIGHT_GRAY);
+
+      // create a map view
+      mapView = new MapView();
+
+      // show progress indicator while map view is drawing
+      mapView.addDrawStatusChangedListener(drawStatusChangedEvent ->
