+        return false;
+    }
+
+    @Override
+    public boolean hasFullRecordScan() {
+        return false;
+    }
+
+    @Override
+    public boolean hasIndexScan(@Nonnull String indexName) {
+        return indexPlans.stream().anyMatch(p -> p.hasIndexScan(indexName));
+    }
+
+    @Nonnull
+    @Override
+    public Set<String> getUsedIndexes() {
