+        if (markReadable) {
+            return buildFuture.thenCompose(vignore ->
+                    getRunner().runAsync(context -> openRecordStore(context)
+                            .thenCompose(store -> store.markIndexReadable(common.getIndex()))
+                            .thenApply(ignore -> null)));
+        } else {
