+    // example, the error code associated with this FDBException.
+    @Nullable
+    private FDBException getFDBException(@Nullable Throwable e) {
+        Throwable curr = e;
+        while (curr != null) {
+            if (curr instanceof FDBException) {
+                return (FDBException)curr;
+            } else {
+                curr = curr.getCause();
+            }
+        }
+        return null;
+    }
