+                                .setMacAddress(e.getMacAddress())
+                                .setRevisionNumber(1)
+                                .addAllFixedIps(fixedIps)
+                                .buildPartial();
+                        if (hostinfo != null) {
+                          for (Port.PortConfiguration.HostInfo h : hostinfo)
+                            portConfiguration =
+                                portConfiguration
+                                    .toBuilder()
+                                    .setNetworkTypeValue(
+                                        Common.OperationType.NEIGHBOR_CREATE_UPDATE_VALUE)
+                                    .setHostInfo(h)
