+                serverList,
+                XdsClient.XdsChannelFactory.getInstance(),
+                node,
+                syncContext,
+                timeService,
+                new ExponentialBackoffPolicy.Provider(),
+                GrpcUtil.STOPWATCH_SUPPLIER);
+          }
+        };
+    xdsClientPool = new XdsClient.RefCountedXdsClientObjectPool(xdsClientFactory);

