+@import '../abstracts/colors.less';
+@import '../abstracts/font.less';
+
+#tasks {
+  margin-bottom: 20px;
+  display: inline-block;
+
+  // TODO: investigate this on the git history to see if removing it will
+  // cause a problem
+  max-width: 325px;
+
+  // The task list is touching the side of the screen. This should be removed
+  // once .side-panel loses it's padding.
+  width: calc(100% + 15px);
+  margin-left: -15px;
+}
+
+#tasks .task {
+  display: flex;
+  align-items: center;
+  flex-wrap: wrap;
+  overflow: hidden;
+  white-space: nowrap;
+  text-overflow: ellipsis;
+  position: relative;
+  font-size: @font-size-sm;
+  font-weight: 500;
+}
+.task.disabled {
+  opacity: 0.5;
+  background-color: transparent;
+}
+
+.task-link-wrapper {
+  display: flex;
+  align-items: center;
+  width: 100%;
+  padding: 2px 0;
+}
+
+.task-link,
+.task-disabled-link {
+  display: inline-flex;
+  flex: 1;
+  align-items: center;
+  padding: 0.5rem 0 0.5rem 1.25rem;
+  max-width: 100%;
+}
+.task-link:link,
+.task-link:visited {
+  color: @text-color;
+  text-decoration: none;
+
+  &:hover,
+  &:focus {
+    background-color: #F8F8F8;
+  }
+
+  &:active {
+    background-color: #EAEFF2;
+  }
+
+  &:focus {
+    outline: 0;
+    // Use a box-shadow instead of an outline or a border because the alternatives
+    // are hidden behind the parent container
+    box-shadow: inset 0 0 0 3px @focus;
+  }
+}
+.task-link--active {
+  background-color: #EAEFF2;
+  position: relative;
+
+  &:before {
+    position: absolute;
+    left: 0;
+    height: 100%;
+    content: '';
+    border-left: 4px solid @primary;
+  }
+}
+.task-link--active:link,
+.task-link--active:visited {
+  &:hover,
+  &:focus {
+    background-color: #EAEFF2;
+  }
+}
+
+// Target only icons withink task link in order to keep backwards compatibility
+.task .task-icon-link {
+  display: inline-block;
+  margin: 0;
+  margin-right: 0.5rem;
+}
+// Reset the margin for the icon links
+.task .task-icon-link img,
+.task img.task-icon-link {
+  margin: 0;
+  // Dimensions have !important to override inline styles set by <l:icon>
+  height: 1.5rem !important;
+  width: 1.5rem !important;
+}
+
+.task-link-text {
+  overflow: hidden;
+  white-space: nowrap;
+  text-overflow: ellipsis;
+  display: inline-block;
+}
+
+.subtasks {
+  padding-left: 1.25rem;
+  width: 100%;
+}
+.subtasks .task {
+  &:first-of-type {
+    border-top: 2px solid #EAEFF2;

