        ExampleConsoleReader consoleReader = new ExampleConsoleReader();
        consoleReader.readLines();
       //  assertEquals(in.capturedLines(), "line1", "line2"); // Fails
      //  assertEquals(in.capturedLines(), "line1", "line2", "line3"); // Correct
    }

