                  - name: Run crossdc-server tests



                    if: ${{ github.event_name != 'pull_request' || env.GIT_DIFF != 0 }}



                    run: mvn clean install -B -nsu -Pcache-server-infinispan,auth-servers-crossdc-jboss,auth-server-wildfly "-Dtest=org.keycloak.testsuite.crossdc.**.*" | misc/log/trimmer.sh; exit ${PIPESTATUS[0]}



                  - name: Run crossdc-adapter tests



                    if: ${{ github.event_name != 'pull_request' || env.GIT_DIFF != 0 }}



                    run: mvn clean install -B -nsu -Pcache-server-infinispan,auth-servers-crossdc-jboss,auth-server-wildfly,app-server-wildfly "-Dtest=org.keycloak.testsuite.adapter.**.crossdc.**.*" | misc/log/trimmer.sh; exit ${PIPESTATUS[0]}



                  - name: Run crossdc-server tests



                    if: ${{ github.event_name != 'pull_request' || env.GIT_DIFF != 0 }}



                    run: mvn install -B -nsu -Pcache-server-infinispan,auth-servers-crossdc-jboss,auth-server-wildfly,app-server-wildfly "-Dtest=org.keycloak.testsuite.crossdc.**.*,org.keycloak.testsuite.adapter.**.crossdc.**.*" | misc/log/trimmer.sh; exit ${PIPESTATUS[0]}

