+        if(jobs == null || jobs.isEmpty()) {
+            return;
+        }
+        if(!clusterService.isCoordinator()) {
+            return;
+        }
+        logger.info("Node left in cluster {} node {}, failing over and requeuing {}", node.getServerId(), node, Arrays.toString(jobs.toArray()));
+        jobs.forEach(jobId -> executorService.requeueById(jobId));
