+                                 "The staging repository can be found at: ${getDeployProperty('staging-repo.url')}\n" +
+                                 "Approve the promotion here: ${env.BUILD_URL}input",
+                                 subject: "${pipelineName} #${getBuildBranch()}",
+                                 to: ZULIP_EMAIL
