+                if (enabledProtocols != null) {
+                    sslContextBuilder.protocols(enabledProtocols.toArray(EMPTY_PROTOCOL_NAMES));
+                }
+                final List<String> ciphers = ssl.getCiphers();
+                if (ciphers != null) {
+                    sslContextBuilder.ciphers(ImmutableList.copyOf(ciphers),
