final class DefaultServletOutputStream extends ServletOutputStream {
    private final DefaultServletHttpResponse response;
    private final List<ByteBuf> byteBufs = new ArrayList<>();

    DefaultServletOutputStream(DefaultServletHttpResponse response) {
        requireNonNull(response, "response");
        this.response = response;
    }

    @Override
    public boolean isReady() {
        return response.getResponseWriter().isOpen();
    }

    @Override
    public void setWriteListener(WriteListener listener) {
        throw new UnsupportedOperationException("Not supported yet.");
    }

    @Override
    public void close() throws IOException {
        if (!byteBufs.isEmpty()) {
            response.getResponseWriter().write(
                    HttpData.wrap(Unpooled.wrappedBuffer(byteBufs.toArray(new ByteBuf[0]))));
            byteBufs.clear();
        }
    }

    @Override
    public void flush() throws IOException {
    }

    @Override
    public void write(int b) throws IOException {
        byteBufs.add(Unpooled.copyInt(b));
    }

    @Override
    public void write(byte[] b, int off, int len) throws IOException {
        requireNonNull(b, "b");
        checkArgument(off >= 0, "off: %s (expected: >= 0)", off);
        checkArgument(len >= 0, "len: %s (expected: >= 0)", len);
        byteBufs.add(Unpooled.copiedBuffer(b, off, len));
    }

    @Override
    public void write(byte[] b) throws IOException {
        requireNonNull(b, "b");
        byteBufs.add(Unpooled.copiedBuffer(b));
    }
}
