+            consulClient.deregister(serviceId)
+                        .aggregate()
+                        .handle((res, cause) -> {
+                      if (cause != null) {
+                          logger.warn("Failed to deregister {} from Consul: {}",
+                                      serviceId, consulClient.uri(), cause);
+                      }

