switch (key) {
           // _owner_name is primitive, so we can initialize the field
            case "owner_name":
                _owner_name = value instanceOf String.class ? (String) value : null;
                return null;
            case "acls":
               // _acls is ACLArray so no need to wrap unless the field is accessed. 
                _acls = null;
                return null;
        }
        return null;

