+        g2D.drawLine((int) startX, (int) startY, (int) midPointX, (int) midPointY);
+        g2D.drawLine((int) midPointX, (int) midPointY, (int) endPointX, (int) midPointY);
+        g2D.drawString(text, (int) endPointX, (int) midPointY);
