+                if ((facility != null) && (facility.isVisible() || currentTrack.isGmRevealed())) {
+                    g2D.setColor(facility.getOwner() == ForceAlignment.Allied ? Color.GREEN : Color.RED);
+                    g2D.drawPolygon(facilityMarker);
