+    /**
+     * Worker function to render force icons to the given surface.
+     */
+    private void drawForces(Graphics2D g2D) {
+        Polygon forceMarker = new Polygon();
+        int xRadius = HEX_X_RADIUS / 3;
+        int yRadius = HEX_Y_RADIUS / 3;
