+    /**
+     * Go to the origin of the hex board and reset the scaling.
+     */
+    private void performInitialTransform(Graphics2D g2D) {
+        g2D.translate(0, 0 + HEX_Y_RADIUS);
+        g2D.scale(scale, scale);
+    }
