+    public void mouseReleasedHandler(MouseEvent e) {
+        if(e.getSource() != this) {
+            return;
+        }
+
+        // left button generally selects a hex
+        if (e.getButton() == MouseEvent.BUTTON1) {        
+            clickedPoint = e.getPoint();
+            boolean pointFoundOnBoard = detectClickedHex();
