+    public enum ReinforcementEligibilityType {
+        /**
+         * Nothing
+         */
+        None,
+
+        /**
+         * Lance is already deployed to the track
+         */
+        ChainedScenario,
+
+        /**
+         * We pay a support point or convert a victory point to a support point
+         */
+        SupportPoint,
+
+        /**
+         * The lance's deployment orders are "Fight"
+         */
+        FightLance
+    }
