+        for (int forceID : scenario.getPlayerTemplateForceIDs()) {
+            Force force = campaign.getForce(forceID);
+            force.clearScenarioIds(campaign, true);
+            force.setScenarioId(scenario.getBackingScenarioID());
+        }
