+package io.opentelemetry.instrumentation.api.tracer.utils;
+
+import io.opentelemetry.trace.Span;
+import io.opentelemetry.trace.attributes.SemanticAttributes;
+import java.net.URI;
+
+public class HttpUrlUtils {
+
+  public static void setHttpUrlAttribute(Span span, URI url) {
+    if (url != null) {
+      StringBuilder urlBuilder = new StringBuilder();
+      if (url.getScheme() != null) {
+        urlBuilder.append(url.getScheme());
+        urlBuilder.append("://");
+      }
+      if (url.getHost() != null) {
+        urlBuilder.append(url.getHost());
+        if (url.getPort() > 0 && url.getPort() != 80 && url.getPort() != 443) {
+          urlBuilder.append(":");
+          urlBuilder.append(url.getPort());
+        }
+      }
+      String path = url.getPath();
+      if (path.isEmpty()) {
+        urlBuilder.append("/");
+      } else {
+        urlBuilder.append(path);
+      }
+      String query = url.getQuery();
+      if (query != null) {
+        urlBuilder.append("?").append(query);
+      }
+      String fragment = url.getFragment();
+      if (fragment != null) {
+        urlBuilder.append("#").append(fragment);
+      }
+
+      span.setAttribute(SemanticAttributes.HTTP_URL.key(), urlBuilder.toString());
+    }
+  }
+}

