+  public void pre(Span span, Exchange exchange, Endpoint endpoint) {
+    span.setAttribute("camel.uri", URISupport.sanitizeUri(endpoint.getEndpointUri()));
+  }
+
+  @Override
+  public void post(Span span, Exchange exchange, Endpoint endpoint) {
+    if (exchange.isFailed()) {
+      span.setAttribute("error", true);
+      if (exchange.getException() != null) {
+        Attributes logEvent =
+            Attributes.newBuilder()
+                .setAttribute("error.kind", "Exception")
+                .setAttribute("message", exchange.getException().getMessage())
+                .build();
+        span.addEvent("error", logEvent);

