+      out.write("<td class=\"align-right\"><pre class=\"no-margin wrap-text\">");
+    } else {
+      formatter.format(
+          "<td class=\"align-right\"><pre class=\"no-margin wrap-text\">%04d/%02d/%02d-",
+          calendar.get(Calendar.YEAR),
+          calendar.get(Calendar.MONTH) + 1,
