*/
const convertFields = (input: unknown, targetFields: string[], recursive: boolean, converter: (value: T) => R?): void =>
  Object.entries( input ).forEach((field, value}) => {
    if(typeof value === "object" && recursive) {
      convertFields(value, targetFields, recursive, converter);
    }
    else {
      targetFields.each( targetField => {
        if(field === targetField) {
          input[field] = converter(value);
        }
      });
    }
  }
