side-effect.ts
import axios from 'axios'

// side effect, this runs when the script is imported
axios.get('https://example.com')

export interface Example {}
example.ts
// any import will run the side effect.
// meaning example.com will be called, and all of axios gets pulled in.
import 'side-effect'

// including if the import is just a typing.
// this also will call example.com and pull in axios
import { Example } from 'side-effect'

// unless the compiler is told to skip the side effects and only get the types.
// this does not call example.com nor does it import axios
import type { Example } from 'side-effect'

