+        LABEL_VALUE_CONVERTERS.put(Float.class, new FloatConverter());
+        LABEL_VALUE_CONVERTERS.put(Ptec.class, new PtecConverter());
+        LABEL_VALUE_CONVERTERS.put(Hhphc.class, new HhphcConverter());
