+    private boolean isHeaderFrame(final String line) {
+        // A new teleinfo trame begin with '3' and '2' bytes (END OF TEXT et START OF TEXT)
+        return (line != null && line.length() > 1 && line.codePointAt(0) == 3 && line.codePointAt(1) == 2);
+    }
