+            }
+            if (isInitialized() && (receiveThreadRef == null || !receiveThreadRef.isAlive())) {
+                updateStatus(ThingStatus.UNKNOWN, ThingStatusDetail.NONE, ERROR_UNKNOWN_RETRY_IN_PROGRESS);
