+        url.append("?grant_type=ecobeePin");
+        url.append("&code=").append(code);
+        url.append("&client_id=").append(apiKey);
+
+        logger.trace("EcobeeAuth: Posting token URL={}", url.toString());
+        String response = executeUrl("POST", url.toString());
