+                                    + MIN_VOLUME;
+                            logger.debug("Got volume command {} zone {}", value, zone);
+                            connector.sendCommand(zone, MonopriceAudioCommand.VOLUME, value);
+                            ZONE_DATA_MAP.get(zone.getZoneId()).setVolume(String.format("%02d", value));
+                        }
+                        break;
