+                            }
+                        }
+                        break;
+                    case CHANNEL_TYPE_BASS:
+                        if (command instanceof DecimalType) {
+                            int value = ((DecimalType) command).intValue();
+                            if (value >= MIN_TONE && value <= MAX_TONE) {
+                                logger.debug("Got bass command {} zone {}", value, zone);
