+            return DateTimeType.valueOf(date);
+        } else if (dateTimeParts.length == 3) { // date - we'll try the device's date format
+            switch (this.dateFormat) {
+                case 0: // dd.mm.yyyy
+                    dateTime = dateTimeParts[2] + "-" + dateTimeParts[1] + "-" + dateTimeParts[0];
