+                case 1: // mm.dd.yyyy
+                    dateTime = dateTimeParts[2] + "-" + dateTimeParts[0] + "-" + dateTimeParts[1];
+                    break;
+                case 2: // yyyy.mm.dd
+                    dateTime = dateTimeParts[0] + "-" + dateTimeParts[1] + "-" + dateTimeParts[2];
