+        return "javasound";
+    }
+
+    @Override
+    public String getLabel(Locale locale) {
+        return "System Microphone";
+    }
+
+    @Override
+    public Set<AudioFormat> getSupportedFormats() {
+        return Collections.singleton(audioFormat);

