+    /**
+     * Utility method to format the various URLs used during the tests
+     *
+     * @param path
+     * @return
+     */
+    private String generateURL(String path) {
+        if (!path.startsWith("/")) {
+            path = "/" + path;
+        }
+        return "http://" + server.getHostname() + ":" + server.getHttpDefaultPort() + "/" + APP_NAME + path;
+    }
