+	 */
+	private byte[] getZip64EoCDLocator(RandomAccessFile file) throws IOException {
+
+		if ( !isZip64() ) {
+			return null;
+		}
+
+	    byte[] block = getBlockFromEndOfFile(file, 
+	    		                             _eocdRecordSize + ZIP64_LOCATOR_SIZE, 
+	    		                             ZIP64_LOCATOR_SIZE);	
