                .change(function(evt) { self._onChangeGotoPage(this, evt); })



                /* Don't fire the GotoPage until the user has stopped typing or pressing keys...







                // .change(function(evt) { self._onChangeGotoPage(this, evt); })



                // .keydown(function(evt) { self._onKeyDownGotoPage(this, evt); })







               // So, we check the keyup, and delay for some configurable Timeout, default .7 secs, and then attempt to GotoPage...



                .bind('keyup', function(evt) {self._onChangeGotoPage(this, evt); })






