+    --ifm-code-color: var(--ifm-color-primary-light);
+  }
+
+
+
+  /******* SIDE BAR STYLES *******/
+
+  .menu__link--sublist {
+    margin-top: 10px;
+    padding-bottom: 25px;
+    padding-top: 25px;
+  }
+
+
+
+  /******* MARKDOWN STYLES *******/
+
+  .alert {
+    border-radius: 0;
+    border: 0;
+    box-shadow: 0px 0px 10px 0px var(--ifm-panel-box-shadow-color);
