+    background-color: var(--ifm-panel-background-color);
+    color: var(--ifm-font-color-secondary);
+  }
+
+  .admonition-icon svg {
+    fill: var(--ifm-font-color-secondary);
+  }
+
+  .alert a {
+    color: var(--ifm-font-color-secondary);
+  }
+
+  .markdown h4{
+    margin-top: 40px;
+    font-size: 20px;
+  }
+
+  .markdown h3{
+    margin-top: 45px;
+    font-size: 29px;
+  }
+
+  .markdown h2{
+    margin-top: 50px;
+    font-size: 35px;
+  }
+
+  .markdown .menuItems {
+    font-family: var(--ifm-font-family-monospace);
+    font-size: var(--ifm-code-font-size);
+    color: var(--ifm-color-primary-darkest);
+    display: inline-block;
+    background-color: #c8daf7;

