+import de.dennisguse.opentracks.util.ExportUtils;
+
+public class ExportService extends JobIntentService {
+
+    private static final int JOB_ID = 1;
+
+    private static final String EXTRA_RECEIVER = "extra_receiver";
+    private static final String EXTRA_TRACK_ID = "extra_track_id";
+    private static final String EXTRA_TRACK_FILE_FORMAT = "extra_track_file_format";
+    private static final String EXTRA_DIRECTORY_URI = "extra_directory_uri";
+    private static final String EXTRA_CONFLICT_DECISION = "extra_conflict_decision";
+
+    public static final int CONFLICT_UNKNOWN = 0;
+    public static final int CONFLICT_OVERWRITE = 1;
+    public static final int CONFLICT_SKIP = 2;
+
+    public static void enqueue(Context context, ExportServiceResultReceiver receiver, Track.Id trackId, TrackFileFormat trackFileFormat, Uri directoryUri, int conflictDecision) {
+        Intent intent = new Intent(context, JobService.class);
+        intent.putExtra(EXTRA_RECEIVER, receiver);
+        intent.putExtra(EXTRA_TRACK_ID, trackId);
+        intent.putExtra(EXTRA_TRACK_FILE_FORMAT, trackFileFormat);
+        intent.putExtra(EXTRA_DIRECTORY_URI, directoryUri);
+        intent.putExtra(EXTRA_CONFLICT_DECISION, conflictDecision);
+        enqueueWork(context, ExportService.class, JOB_ID, intent);
+    }
+
+    @Override
+    protected void onHandleWork(@NonNull Intent intent) {
+        // Get all data.
+        ResultReceiver resultReceiver = intent.getParcelableExtra(EXTRA_RECEIVER);
+        Track.Id trackId = intent.getParcelableExtra(EXTRA_TRACK_ID);
+        TrackFileFormat trackFileFormat = (TrackFileFormat) intent.getSerializableExtra(EXTRA_TRACK_FILE_FORMAT);
+        Uri directoryUri = intent.getParcelableExtra(EXTRA_DIRECTORY_URI);
+        int conflictDecision = intent.getIntExtra(EXTRA_CONFLICT_DECISION, CONFLICT_UNKNOWN);
+
+        // Build directory file.
+        DocumentFile directoryFile = DocumentFile.fromTreeUri(this, directoryUri);
+
+        // Checks if the track is already exported in the directory.
+        boolean fileAlreadyExists = ExportUtils.existsExportDocumentFile(trackId, trackFileFormat.getExtension(), directoryFile);
+
+        // Prepare resultCode and bundle to send to the receiver.
+        int resultCode = getResultCode(fileAlreadyExists, conflictDecision);
+        Bundle bundle = new Bundle();
+        bundle.putParcelable(ExportServiceResultReceiver.RESULT_EXTRA_TRACK_ID, trackId);
+        bundle.putBoolean(ExportServiceResultReceiver.RESULT_EXTRA_SUCCESS, false);
+
+        // If file doesn't exists or exists but conflict decision is overwrite then export the track.
+        if (!fileAlreadyExists || (fileAlreadyExists && conflictDecision == CONFLICT_OVERWRITE)) {
+            ContentProviderUtils contentProviderUtils = new ContentProviderUtils(this);
+            Track track = contentProviderUtils.getTrack(trackId);
+            boolean success = ExportUtils.exportTrack(this, trackFileFormat, directoryFile, track);
+            bundle.putBoolean(ExportServiceResultReceiver.RESULT_EXTRA_SUCCESS, success);
+        }
+
+        // Send result to the receiver.
+        resultReceiver.send(resultCode, bundle);
+    }
+
+    private int getResultCode(boolean fileExists, int conflictDecicions) {
+        if (!fileExists) {
+            return ExportServiceResultReceiver.RESULT_EXPORTED;
+        }
+
+        switch (conflictDecicions) {
+            case CONFLICT_OVERWRITE:
+                return ExportServiceResultReceiver.RESULT_OVERWRITTEN;
+            case CONFLICT_SKIP:
+                return ExportServiceResultReceiver.RESULT_SKIPPED;
+            default:
+                return ExportServiceResultReceiver.RESULT_UNKNOWN;

