+    p.setProperty("domain_name", domainUid);
+    p.setProperty("domain_uid", domainUid);
+    p.setProperty("cluster_name", clusterName);
+    p.setProperty("admin_server_name", adminServerName);
+    p.setProperty("managed_server_port", "8001");
