++        villagerTraderSpawnAttemptDelay = getInt("wandering_trader.spawn-attempt-delay", villagerTraderSpawnAttemptDelay);
++        villagerTraderSpawnChanceFailureIncrement = getInt("wandering_trader.spawn-chance-failure-increment", villagerTraderSpawnChanceFailureIncrement);
++        villagerTraderSpawnChanceMin = getInt("wandering_trader.spawn-chance-min", villagerTraderSpawnChanceMin);
++        villagerTraderSpawnChanceMin = getInt("wandering_trader.spawn-chance-max", villagerTraderSpawnChanceMax);
++    }
+ }
