+package tc.oc.pgm.fireworks;
+
+import com.google.common.base.Preconditions;
+import com.google.common.collect.ImmutableList;
+import java.util.Collection;
+import java.util.Collections;
+import java.util.List;
+import java.util.Set;
+import java.util.stream.Collectors;
+import javax.annotation.Nonnull;
+import org.bukkit.Bukkit;
+import org.bukkit.Color;
+import org.bukkit.FireworkEffect;
+import org.bukkit.FireworkEffect.Type;
+import org.bukkit.Location;
+import org.bukkit.Material;
+import org.bukkit.World;
+import org.bukkit.block.Block;
+import org.bukkit.entity.EntityType;
+import org.bukkit.entity.Firework;
+import org.bukkit.event.EventHandler;
+import org.bukkit.event.EventPriority;
+import org.bukkit.event.Listener;
+import org.bukkit.inventory.meta.FireworkMeta;
+import tc.oc.pgm.Config;
+import tc.oc.pgm.api.match.Match;
+import tc.oc.pgm.api.match.MatchModule;
+import tc.oc.pgm.api.match.MatchScope;
+import tc.oc.pgm.api.match.event.MatchFinishEvent;
+import tc.oc.pgm.api.party.Competitor;
+import tc.oc.pgm.api.player.MatchPlayer;
+import tc.oc.pgm.controlpoint.events.ControllerChangeEvent;
+import tc.oc.pgm.core.CoreLeakEvent;
+import tc.oc.pgm.destroyable.DestroyableDestroyedEvent;
+import tc.oc.pgm.events.ListenerScope;
+import tc.oc.pgm.flag.event.FlagCaptureEvent;
+import tc.oc.pgm.regions.Bounds;
+import tc.oc.pgm.regions.Region;
+import tc.oc.pgm.wool.PlayerWoolPlaceEvent;
+import tc.oc.util.bukkit.BukkitUtils;
+import tc.oc.util.bukkit.block.BlockVectors;
+
+@ListenerScope(MatchScope.LOADED)
+public class FireworkMatchModule implements MatchModule, Listener {
+
+  private final Match match;
+
+  private final boolean postEnabled = Config.Fireworks.postMatchEnabled();
+  private final boolean goalsEnabled = Config.Fireworks.goalsEnabled();
+  private final int number = 5;
+  private final int delay = 40;
+  private final int frequency = 40;
+  private final int iterationCount = 15;
+  private final int power = 2;
+
+  public FireworkMatchModule(Match match) {
+    this.match = match;
+  }
+
+  @EventHandler(priority = EventPriority.MONITOR)
+  public void onMatchEnd(final MatchFinishEvent event) {
+    if (!postEnabled) return;
+    match
+        .getScheduler(MatchScope.LOADED)
+        .runTaskTimer(delay, frequency, new FireworkRunner(event.getWinners()));
+  }
+
+  public static List<FireworkEffect.Type> AVAILABLE_TYPES =
+      ImmutableList.<FireworkEffect.Type>builder()
+          .add(Type.BALL)
+          .add(Type.BALL_LARGE)
+          .add(Type.BURST)
+          .add(Type.STAR)
+          .build();
+
+  public class FireworkRunner implements Runnable {
+    private final Set<Color> colors;
+    private final Collection<Competitor> winners;
+    private int iterations = 0;
+
+    public FireworkRunner(Collection<Competitor> winners) {
+      this.winners = winners;
+      this.colors =
+          winners.stream()
+              .map(winner -> BukkitUtils.colorOf(winner.getColor()))
+              .collect(Collectors.toSet());
+    }
+
+    @Override
+    public void run() {
+      if (this.iterations < iterationCount) {
+        // Build this list fresh every time, because MatchPlayers can unload, but Competitors can't.
+        final List<MatchPlayer> players =
+            winners.stream().flatMap(c -> c.getPlayers().stream()).collect(Collectors.toList());
+        Collections.shuffle(players);
+
+        for (int i = 0; i < players.size() && i < number; i++) {
+          MatchPlayer player = players.get(i);
+
+          Type type = AVAILABLE_TYPES.get(match.getRandom().nextInt(AVAILABLE_TYPES.size()));
+
+          FireworkEffect effect =
+              FireworkEffect.builder()
+                  .with(type)
+                  .withFlicker()
+                  .withColor(this.colors)
+                  .withFade(Color.BLACK)
+                  .build();
+
+          spawnFirework(player.getBukkit().getLocation(), effect, power);
+        }
+      }
+      this.iterations++;
+    }
+  }
+
+  @EventHandler(priority = EventPriority.MONITOR)
+  public void onWoolPlace(final PlayerWoolPlaceEvent event) {
+    if (goalsEnabled && event.getWool().isVisible()) {
+      this.spawnFireworkDisplay(
+          BlockVectors.center(event.getBlock()), event.getWool().getDyeColor().getColor(), 6, 2, 2);
+    }
+  }
+
+  @EventHandler(priority = EventPriority.MONITOR)
+  public void onCoreLeak(final CoreLeakEvent event) {
+    if (goalsEnabled && event.getCore().isVisible()) {
+      this.spawnFireworkDisplay(
+          event.getMatch().getWorld(),
+          event.getCore().getCasingRegion(),
+          event.getCore().getColor(),
+          8,
+          1.5,
+          2);

