+
+  const {
+    updateAnalysesStatsTimePeriod,
+    updateProjectStatsTimePeriod,
+    updateSampleStatsTimePeriod,
+    updateUserStatsTimePeriod,
+    getChartConfig
+  } = useContext(AdminStatisticsContext);
+
