+
+        return messages.stream()
+            .map(Message::body)
+            .map(this::toQuark)
+            .collect(Collectors.toList());
+    }
+
+    private Quark toQuark(String message) {
+        Quark quark = null;
+        try {
+            quark = QUARK_READER.readValue(message);
+        } catch (Exception e) {
