+        ControllerAnnotator.addPut(methodCreator);
+        ControllerAnnotator.addPath(methodCreator, EntityUrlProvider.getCollectionUrl(getEntityType()) + "/{id}");
+        ControllerAnnotator.addPathParam(methodCreator.getParameterAnnotations(0), "id");
+        ControllerAnnotator.addConsumes(methodCreator, ControllerAnnotator.APPLICATION_JSON);
+        ControllerAnnotator.addProduces(methodCreator, ControllerAnnotator.APPLICATION_HAL_JSON);
