+         *pPtrConnection_ = pConnection;
+         return Success();
+      }
+      catch (soci::soci_error& error)
+      {
+         return DatabaseError(error);
+      }
+   }
+
+private:
+   boost::shared_ptr<IConnection>* pPtrConnection_;
+};
