+            long timeDifferenceInMilliSeconds = DaysOld * 1000 * 60 * 60 * 24;
+            jsonObject.put(LAST_UPDATED_TIME, System.currentTimeMillis() - timeDifferenceInMilliSeconds);
+            return jsonObject.toString();
+        } catch (JSONException e) {
+            e.printStackTrace();
+            return null;
